import React from "react";
import { Form, Formik } from 'formik';
import { Link } from "react-router-dom";
import * as Yup from 'yup';

import MyTextField from '../../common/textfield'
import "./username.scss";

export default function Username({verifyUsername}) {
    return (
        <div className='signin'>
            <div className="container">
                <div className="mainTitle">Sign in</div>

                <Formik
                    initialValues={{ username: '' }}

                    validationSchema={SigninUsernameSchema}

                    onSubmit={(values, actions, { setErrors, resetForm }) => {
                        setErrors()
                        setTimeout(() => {
                            alert(JSON.stringify(values, null, 2));
                            actions.setSubmitting(false);
                        }, 1000);
                    }}
                >
                    {(props) => (
                        <Form>
                            <MyTextField name="username" type="text" label="Username" />
                            <button type="submit">Submit</button>
                        </Form>
                    )}
                </Formik>
            </div>
            <div className="centerText commonLabels">
                New to Autodesk ?  <Link to="/">Create account</Link>
            </div>

            <div className="footer commonLabels">
                <p>Your account for everything Autodesk</p>
                <a href={''} >Learn More</a>
            </div>
        </div>
    );
}

const SigninUsernameSchema = Yup.object().shape({
    username: Yup.string().required('Please enter a valid username')
});
