import React from 'react';
import { useField, Form, FormikProps, Formik } from 'formik';

import {
    Link
  } from "react-router-dom";

import MyTextField from '../common/Textfield'
import * as Yup from 'yup';
import "./account.scss";

const Account = () => (
    <div className='account'>
        <div className="container">
            <div className="mainTitle">  Create account </div>
            <Formik
                initialValues={{
                    firstName: '',
                    lastName: '',
                    username: '',
                    retypeUsername: '',
                    password: '',
                    retypePassword: ''
                }}

                validationSchema={SignupSchema}

                onSubmit={(values, actions) => {
                    setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        actions.setSubmitting(false);
                    }, 1000);
                }}
            >
                {(props) => (
                    <Form>
                        <div className="firstnameWrapper">
                            <MyTextField name="firstName" type="text" label="First Name" />
                        </div>
                        <div className='lastnameWrapper'>
                            <MyTextField name="lastName" type="text" label="Last Name" />
                        </div>
                        <MyTextField name="username" type="text" label="Username" />
                        <MyTextField name="retypeUsername" type="text" label="Re-type username" />
                        <MyTextField name="password" type="text" label="Password" />
                        <MyTextField name="retypePassword" type="text" label="Re-type password" />
                        <button type="submit">Submit</button>
                    </Form>
                )}
            </Formik>
            <div className="centerText commonLabels alreayAccount">
                Already have an account ? <a href={''} >Signin</a>
            </div>

            <div className="footer commonLabels">
                <p>Your account for everything Autodesk</p>
                <a href={''} >Learn More</a>
            </div>
        </div>
    </div>
);

const SignupSchema = Yup.object().shape({
    firstName: Yup.string().required('Please enter your first name'),
    lastName: Yup.string().required('Please enter your last name'),
    username: Yup.string().required('Please enter valid username'),
    retypeUsername: Yup.string().required('please enter same username'),
    password: Yup.string().required('please enter valid password'),
    retypePassword: Yup.string().required('please enter same password'),

  });

export default Account
